mapper.insert:
INSERT INTO subject_score (
    subject_code
    , subject_score
    , student_id
) VALUES (
    #{subjectCode}
    , 0
    , #{studentId}
)

mapper.update:
UPDATE subject_score
   SET subject_score = #{subjectScore}
 WHERE student_id = #{studentId}
   AND subject_code = #{subjectCode}
